    [_cssVariableGridBasicSpanOneThird]: getColumnSpan(2),
    [_cssVariableGridBasicSpanTwoThirds]: getColumnSpan(4),
    [_cssVariableGridNarrowSpanOneHalf]: getColumnSpan(3),
    display: 'grid',
